let state = {
    profilePage: {
        posts: [
            { id: 1, message: "It's my first post", like: "25" },
            { id: 2, message: "It's my second post", like: "30" }
        ]
    },
    dialogsPage: {
        messages: [
            { id: 1, message: 'Hi' },
            { id: 2, message: 'How are you?' },
        ], 
        dialogs: [
            { id: 1, name: 'Aizhan' },
            { id: 2, name: 'Aisara' },
            { id: 3, name: 'Karro' },
            { id: 4, name: 'Ahmad' }
        ]
    },
    sidebar: {
        friends: [
            { id: 1, name: 'Margo' },
            { id: 2, name: 'Aisara' },
            { id: 3, name: 'Karro' }
        ]
    }
}

export let addPost = (postMessage) => {
    debugger;
    let newPost = {
        id: 5,
        message: postMessage,
        like: "0"
    };

    state.profilePage.posts.push(newPost);
    rerenderEntireTree();
}

export default state;
