// let classes = classes1 + " " + classes2;
// let classesNew_ES6 = `${classes.item} ${classes.active}`;

// console.log(classes);


const Navbar = () => {
    return (
        <nav className={classes.nav}>
            <div className={classes.item}>
                <NavLink to="/profile" className={classes.active}> Profile </NavLink>
                className = { navData => navData.isActive ? s.active : s.item }
            </div>
            <div className={`${classes.item} ${classes.active}`}>
                <NavLink to="/dialogs" className={classes.active}> Messages </NavLink>
            </div>
            <div className={classes.item}>
                <NavLink to="/news" className={classes.active}>News</NavLink>
            </div>
            <div className={classes.item}>
                <NavLink to="/music" className={classes.active}>Music</NavLink>
            </div>
            <div className={classes.item}>
                <NavLink to="/settings" className={classes.active}>Settings</NavLink>
            </div>
        </nav>
    )
}

export default Navbar
