import React from 'react'; //we should import jsx, react is library from node modules
import classes from './Navbar.module.css';
import { NavLink } from "react-router-dom";

// let s={
//     'nav': 'Какое-то название, которое можно увидеть в браузере', 
//     'item': 'some name',
//     'active': 'another name'
// }

// let classes1 = 'item';
// let classes 2 = 'active';
// Comment: to get: 'item active'
// let classes = classes1 + " " + classes2;
// let classesNew_ES6 = `${classes.item} ${classes.active}`;

// console.log(classes);


const Navbar = (props) => {
    debugger;
    return (
        <nav className={classes.nav}>
            <div >
                {/* className={classes.item}  из за нового синтаксис внизу, эту линию можем не указывать*/}
                <NavLink to="/profile" className={navData => navData.isActive ? classes.active : classes.item}> Profile </NavLink>
            </div>
            <div>
                {/* className={`${classes.item} ${classes.active}`} */}
                <NavLink to="/dialogs" className={navData => navData.isActive ? classes.active : classes.item} > Messages </NavLink>
            </div>
            <div >
                {/* className={classes.item} */}
                <NavLink to="/news" className={navData => navData.isActive ? classes.active : classes.item} >News</NavLink>
            </div>
            <div>
                <NavLink to="/music" className={navData => navData.isActive ? classes.active : classes.item} >Music</NavLink>
            </div>
            <div>
                <NavLink to="/settings" className={navData => navData.isActive ? classes.active : classes.item} >Settings</NavLink>
            </div>
            <div>
                <div>
                    <span>{props.state.sidebar} </span>
                </div>
            </div>
        </nav>
    )
}

export default Navbar
