const ADD_POST = 'ADD-POST';
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT';
const UPDATE_NEW_MEESSAGE_BODY = 'UPDATE-NEW-MEESSAGE-BODY';
const SEND_MEESSAGE = 'SEND_MEESSAGE';

let store = {
    _state: {
        profilePage: {
            posts: [
                { id: 1, message: "It's my first post", like: "25" },
                { id: 2, message: "It's my second post", like: "30" }
            ],
            newPostText: 'aizhan.com'
        },
        dialogsPage: {
            messages: [
                { id: 1, message: 'Hi' },
                { id: 2, message: 'How are you?' },
            ],
            dialogs: [
                { id: 1, name: 'Aizhan' },
                { id: 2, name: 'Aisara' },
                { id: 3, name: 'Karro' },
                { id: 4, name: 'Ahmad' }
            ],
            newMessageBody: ""
        },
        sidebar: {
            friends: [
                { id: 1, name: 'Margo' },
                { id: 2, name: 'Aisara' },
                { id: 3, name: 'Karro' }
            ]
        }
    },
    _callSubscriber() {
        console.log('state was changed');
    },

    getState() {
        return this._state;
    },
    subscribe(observer) {
        this._callSubscriber = observer;
    },
    dispatch(action) {

        this._state.profilePage = profileReducer(action, this._state.profilePage);
        this._state.profilePage = profileReducer(action, this._state.profilePage);
        this._state.profilePage = profileReducer(action, this._state.profilePage);

        if (action.type === ADD_POST) {
            let newPost = {
                id: 5,
                message: this._state.profilePage.newPostText,
                like: "0"
            };

            this._state.profilePage.posts.push(newPost);
            this._state.profilePage.newPostText = '';
            this._callSubscriber(this._state);
        }
        else if (action.type === UPDATE_NEW_POST_TEXT) {
            this._state.profilePage.newPostText = action.newText;
            this._callSubscriber(this._state);
        } else if (action.type === UPDATE_NEW_MEESSAGE_BODY) {
            this._state.dialogsPage.newMessageBody = action.body;
            this._callSubscriber(this._state);
        } else if (action.type === SEND_MEESSAGE) {
            let body =  this._state.dialogsPage.newMessageBody;
            this._state.dialogsPage.newMessageBody = "";
            this._state.dialogsPage.messages.push({ id: 3, message: body});
            this._callSubscriber(this._state);
        }
    }
}

export const addPostActionCreator = () => ({ type: ADD_POST })
export const updateNewPostTextActionCreator = (text) => ({
    type: UPDATE_NEW_POST_TEXT, newText: text
})

export const sendMessageCreator = () => ({ type: SEND_MEESSAGE })
export const updateNewMessageBodyCreator = (body) => ({
    type: UPDATE_NEW_MEESSAGE_BODY, body: body
})


window.store = store;
export default store;
