import React from 'react'; //we should import jsx, react is library from node modules
import classes from './MyPosts.module.css';
import Post from './Post/Post';
import {Field, Form} from "react-final-form";
import {required} from "../../../utils/validators/validators"; //собирать данные и отправить их в handleSubmit


let AddNewPostForm =(props) => {
    return (
        <Form
            onSubmit={props.onSubmit}
            render={({ handleSubmit }) => (
                <form onSubmit={handleSubmit}>
                    <div>
                        <Field component= {"textarea"}name={"newPostText"} validate={composeValidators(required, maxLength15, minLength2)} />
                    </div>
                    <div>
                        <button>Add post</button>
                    </div>
                </form>
            )}
        />
    )
}

const MyPosts = (props) => {

  let postsElements = props.posts.map(p => <Post id={p.id} key={p.id} message={p.message} like={p.like} />)

  let newPostElement = React.createRef();

  let onAddPost = (values) => {
   props.addPost(values.newPostText);
  }

  return (
    <div className={classes.postsBlock}>
      <h3>My posts</h3>
        <AddNewPostForm onSubmit = {onAddPost}/>
      <div className={classes.posts}>
        {postsElements}
      </div>
    </div>
  )
}

export default MyPosts;
