import React from 'react'; //we should import jsx, react is library from node modules
import classes from './MyPosts.module.css';
import Post from './Post/Post';

const MyPosts = () => {

  let posts = [
    {id: 1, message: "It's my first post", like: "25"},
    {id: 2, message: "It's my second post", like: "30"}
]

let postsElements = posts.map(p=> <Post id={p.id} message={p.message} like={p.like} /> )

  return (
    <div className={classes.postsBlock}>
      <h3>My posts</h3>
      <div>
        {/* New post */}
        <div>
        <textarea></textarea>
        </div>
        <div>
          <button>Add post</button>
        </div>
      </div>
      <div className={classes.posts}>
        {postsElements}
        {/* <Post id={postData[0].id} message={postData[0].message} like={postData[0].like} />
        <Post id={postData[1].id} message={postData[1].message} like={postData[1].like} /> */}
      </div>
    </div>
  )
}

export default MyPosts;
