import React from 'react'; //we should import jsx, react is library from node modules
import classes from './MyPosts.module.css';
import Post from './Post/Post';

const MyPosts = () => {

  let postData = [
    { id: 1, post: "It's my first post"  },
    { id: 2, post: "It's my second post" },
]

  return (
    <div className={classes.postsBlock}>
      <h3>My posts</h3>
      <div>
        {/* New post */}
        <div>
        <textarea name="" id="" cols="30" rows="10"></textarea>
        </div>
        <div>
          <button>Add post</button>
        </div>
      </div>
      <div className={classes.posts}>
        <Post message="It's my first post" like="25" />
        <Post message="It's my second post" like="30" />
      </div>
    </div>
  )
}

export default MyPosts;
