const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';


let initialState = {
    users: [
        { id: 1, photoUrl: 'https://variety.com/wp-content/uploads/2022/02/Screen-Shot-2022-05-09-at-10.04.13-AM.png?w=1000&h=563&crop=1', followed: false, fullName: 'Aizhan', status: 'I am a boss', location: {city: 'Atyrau', country: 'KZ'}},
        { id: 2, photoUrl: 'https://variety.com/wp-content/uploads/2022/02/Screen-Shot-2022-05-09-at-10.04.13-AM.png?w=1000&h=563&crop=1', followed: true, fullName: 'Aisara', status: 'I am a boss too', location: {city: 'Atyrau', country: 'KZ'}}
    ]
};

const usersReducer = (state=initialState, action) => {
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map( u => {
                    if(u.id === action.userId){
                        return {...u, followed: true}
                    }
                    return u;
                    }
                )
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map( u => {
                        if(u.id === action.userId){
                            return {...u, followed: false}
                        }
                        return u;
                    }
                )
            }
        case SET_USERS: {
            return {
                ...state,
                users: [...state.users, ...action.users]
            }
        }
        default:
            return state;
    }
}

export const followAC = (userId) => ({ type: FOLLOW, userId })
export const unfollowAC = (userId) => ({ type: UNFOLLOW, userId })
export const setUserAC = (users) => ({ type: SET_USERS, users })



export default usersReducer;
