import React from "react";
import classes from './Dialogs.module.css';
import DialogItem from './DialogItem/DialogItem';
import Message from './Message/Message';

const Dialogs = () => {

    let dialogs = [
        { id: 1, name: 'Aizhan' },
        { id: 2, name: 'Aisara' },
        { id: 3, name: 'Karro' },
        { id: 4, name: 'Ahmad' }
    ]

    let messages = [
        { id: 1, message: 'Hi'},
        { id: 2, message: 'How are you?' },
    ]

    let dialogsElements = dialogs.map(d=><DialogItem name={d.name} id={d.id} />)
    let messagesElements = messages.map(m=><Message id={m.id} message={m.message} />)


    return (
        <div className={classes.dialogs}>
            <div className={classes.dialogsItems}>
                {dialogsElements}
                {/* <DialogItems name={dialogsData[0].name} id={dialogsData[0].id} />
                <DialogItems name={dialogsData[1].name} id={dialogsData[1].id} /> */}
            </div>
            <div className={classes.messages}>
                {messagesElements}
                {/* <Message id={messages[0].id} message={messages[0].message} />
                <Message id={messages[1].id} message={messages[1].message} /> */}
            </div>
        </div>
    );
}

export default Dialogs;
