import React from 'react'; //we should import jsx, react is library from node modules
import classes from './MyPosts.module.css';
import Post from './Post/Post';

const MyPosts = (props) => {
  //   let posts = [
  //     {id: 1, message: "It's my first post", like: "25"},
  //     {id: 2, message: "It's my second post", like: "30"}
  // ]

  let postsElements = props.posts.map(p => <Post id={p.id} message={p.message} like={p.like} />)

  let newPostElement = React.createRef();

  let addPost = () => {
    let text = newPostElement.current.value;
    props.addPost(text);
    props.updateNewPostText() 
  }

  let onPostChange = () => {
    let text = newPostElement.current.value;
    props.updateNewPostText(text);
  }

  return (
    <div className={classes.postsBlock}>
      <h3>My posts</h3>
      <div>
        <div>
          <textarea ref={newPostElement}
            onChange={onPostChange}
            value={props.newPostText}
            cols="30" rows="10" />
        </div>
        <div>
          <button onClick={addPost}>Add post</button>
        </div>
      </div>
      <div className={classes.posts}>
        {postsElements}
      </div>
    </div>
  )
}

export default MyPosts;
