import React from 'react'; //we should import jsx, react is library from node modules
import classes from './MyPosts.module.css';
import Post from './Post/Post';
import { updateNewPostTextActionCreator, addPostActionCreator } from '../../../redux/profile-reducer';
import * as PropTypes from "prop-types";

function AddNewPostForm(props) {
    return <form>
        <div>
          <textarea ref={props.ref} onChange={props.onChange}
                    value={props.props.newPostText} cols="30" rows="10"/>
        </div>
        <div>
            <button onClick={props.onClick}>Add post</button>
        </div>
    </form>;
}


const MyPosts = (props) => {

  let postsElements = props.posts.map(p => <Post id={p.id} key={p.id} message={p.message} like={p.like} />)

  let newPostElement = React.createRef();

  let onAddPost = (values) => {
   values.addPost();
  }

  return (
    <div className={classes.postsBlock}>
      <h3>My posts</h3>
        <AddNewPostForm onSubmit = {onAddPost}/>
      <div className={classes.posts}>
        {postsElements}
      </div>
    </div>
  )
}

export default MyPosts;
