const UPDATE_NEW_MEESSAGE_BODY = 'UPDATE-NEW-MEESSAGE-BODY';
const SEND_MEESSAGE = 'SEND_MEESSAGE';

let initialState = {
    messages: [
        { id: 1, message: 'Hi' },
        { id: 2, message: 'How are you?' },
    ],
    dialogs: [
        { id: 1, name: 'Aizhan' },
        { id: 2, name: 'Aisara' },
        { id: 3, name: 'Karro' },
        { id: 4, name: 'Ahmad' }
    ],
    newMessageBody: ""
};

const dialogsReducer = (state = initialState, action) => {
    let stateCopy = {
        ...state,
        messages: [...state.messages]
    };

    switch (action.type) {
        case UPDATE_NEW_MEESSAGE_BODY:
            stateCopy.newMessageBody = action.body;
            return stateCopy;
        case SEND_MEESSAGE:
            let body = state.newMessageBody;
            stateCopy.newMessageBody = "";
            stateCopy.messages.push({ id: 3, message: body });
            return stateCopy;
        default:
            return state;
    }

}

export const sendMessageCreator = () => ({ type: SEND_MEESSAGE })
export const updateNewMessageBodyCreator = (body) => ({
    type: UPDATE_NEW_MEESSAGE_BODY, body: body
})

export default dialogsReducer;
